import { Component, Prop, Vue } from 'vue-property-decorator';

@Component
export default class PurchaseRestrictionDialog extends Vue {
    @Prop(String) private readonly warningMsg!: string;

    @Prop(Number) private readonly isDialog!: number;

    public handleOk() {
      this.$emit('getDialogFeedback', this.isDialog, true);
    }

    public handleCancel() {
      this.$emit('getDialogFeedback', this.isDialog, false);
    }
}

import { Component, Prop, Vue } from 'vue-property-decorator';

@Component
export default class Total extends Vue {
    @Prop(Number) private readonly selectListLength!: number;

    @Prop(Number) private readonly vaildCommoditiesNum!: number;

    @Prop(Number) private readonly curTotalPrice!: number;

    private selectAllStatus = false;

    public selectAll() {
      this.$emit('getSelectAllFeedback', this.selectAllStatus);
    }

    public submitOrder() {
      this.$emit('getSubmitOrderFeedback');
    }
}

