import { Dict } from '@/types/types';

export const urls = {
  tmall:
        'data:image/png;base64,',
  enterprise:
        'data:image/jpeg;base64,',
  tmall_internation:
        'data:image/png;base64,',
  tmall_wuyou: '//gw.alicdn.com/tfs/TB10WFoJH9YBuNjy0FgXXcxcXXa-405-72.png',
};

// 获取商铺类型  1-天猫 | 2-企业店铺 | 3-天猫国际
export const storeType: Dict<string> = {
  1: urls.tmall,
  2: urls.enterprise,
  3: urls.tmall_internation,
};

import { Component, Prop, Watch, Vue } from 'vue-property-decorator';
import Overlay from '@/components/Overlay.vue';
import PurchaseRestrictionDialog from '@/components/PurchaseRestrictionDialog.vue';
import Total from '@/components/Total.vue';
import SkuSelector from '@/components/SkuSelector.vue';
import NoCommodities from '@/components/NoCommodities.vue';
import { storeType } from '@/shared/constants';
import {
  ValidCommodity,
  InvalidCommodity,
  SelectedCommodity,
} from '@/types/types';

@Component({
  components: {
    Overlay,
    PurchaseRestrictionDialog,
    Total,
    SkuSelector,
    NoCommodities,
  },
})
export default class Item extends Vue {
    @Prop() private vaildCommodities!: ValidCommodity[];

    @Prop() private invaildCommodities!: InvalidCommodity[];

    @Prop() private vaildCommoditiesNum!: number;

    @Prop() private totalNum!: number;

    private tmall_wuyou_logo =
        'https://gw.alicdn.com/tfs/TB10WFoJH9YBuNjy0FgXXcxcXXa-405-72.png';

    private showDialog = false;

    private warningMsg = '';

    private isDialog = 0;

    private selectList: SelectedCommodity[] = [];

    private selectListLength = 0;

    private curTotalPrice = 0;

    private dialogFeedback = false;

    private showSkuPicker = false;

    private curStoreIndex = 0;

    private curSkuIndex = 0;

    private curSkuId = '';

    get totalPrice() {
      return this.selectList.reduce(
        (val, acc) => acc.curNum * acc.unitPrice,
        0,
      );
    }

    @Watch('selectList', { deep: true })
    handler() {
      this.selectListLength = this.selectList.length;
      this.curTotalPrice = 0;
      for (let i = 0, l = this.selectListLength; i < l; i += 1) {
        this.curTotalPrice +=
                this.selectList[i].curNum * this.selectList[i].unitPrice;
      }
    }

    // 获取 sku 索引
    public getSkuIdIndex(skuId: string) {
      const result = this.selectList.findIndex(
        skuItem => skuItem.skuId === skuId,
      );
      return result;
    }

    // 获取所有选中的 skuId 列表
    public getAllSelectedSkuId() {
      return this.selectList.map(val => val.skuId);
    }

    // 判断一个商品是否被选中
    public isCommoditySelected(skuId: string) {
      return this.selectList.find(val => val.skuId === skuId);
    }

    // 获取一个商铺选中的商品 (sku 列表)
    public getSelectedSkuIdsByStore(storeId: string) {
      const currStore = this.getCurrentStore(storeId);

      if (currStore) {
        return currStore.commodity_list.map(val => val.sku_id);
      }

      return [];
    }

    // 一个商铺是否被选择(点击商铺全选 或者 逐一选中一个商铺的所有商品)
    public isStoreSelected(storeId: string) {
      const skuIds = this.getSelectedSkuIdsByStore(storeId);

      if (skuIds.length > 0) {
        return skuIds.every(skuId => this.getAllSelectedSkuId().includes(skuId));
      }

      return false;
    }

    // 获取当前商铺
    public getCurrentStore(storeId: string) {
      const currStore = this.vaildCommodities.find(
        val => val.store_id === storeId,
      );

      return currStore;
    }

    // 选择单品
    public toggleCommodity(sku: SelectedCommodity) {
      const isCommoditySelected = this.isCommoditySelected(sku.skuId);
      if (isCommoditySelected) {
        this.selectList = this.selectList.filter(
          val => val.skuId !== sku.skuId,
        );
      } else {
        this.selectList.push(sku);
      }
    }

    // 按商铺批量选择
    public toggleStore(storeId: string) {
      const isStoreSelected = this.isStoreSelected(storeId);
      const currStore = this.getCurrentStore(storeId);

      if (currStore) {
        if (isStoreSelected) {
          const selectedSkuIdsByStore = this.getSelectedSkuIdsByStore(
            storeId,
          );
          this.selectList = this.selectList.filter(
            val => !selectedSkuIdsByStore.includes(val.skuId),
          );
        } else {
          currStore.commodity_list.forEach(val => {
            if (!this.isCommoditySelected(val.sku_id)) {
              this.selectList.push({
                skuId: val.sku_id,
                unitPrice: val.sku_unit_price,
                curNum: val.cur_cart_num,
              });
            }
          });
        }
      }
    }

    // 增加单品数量
    public addCount(
      storeIndex: number,
      commodityIndex: number,
      curNum: number,
      limitMaxNum: number,
      maxStockNum: number,
      skuId: string,
    ) {
      // 如果有限购条件
      if (limitMaxNum > 0) {
        // 如果增加一件的话将超过最大限购数
        if (curNum + 1 > limitMaxNum) {
          this.showDialog = true;
          this.warningMsg = '亲，该宝贝不能购买更多哦～';
          this.isDialog = 0;
          this.closeDialog();
        } else {
          this.vaildCommodities[storeIndex].commodity_list[
            commodityIndex
          ].cur_cart_num += 1;

          if (this.getSkuIdIndex(skuId) !== -1) {
            this.selectList[this.getSkuIdIndex(skuId)].curNum += 1;
          }
        }
        // 没有限购条件，数量只要不超过库存量即可
      } else if (curNum + 1 > maxStockNum) {
        this.showDialog = true;
        this.isDialog = 0;
        this.warningMsg = '亲，该宝贝不能购买更多哦～';
        this.closeDialog();
      } else {
        this.vaildCommodities[storeIndex].commodity_list[
          commodityIndex
        ].cur_cart_num += 1;
        if (this.getSkuIdIndex(skuId) !== -1) {
          this.selectList[this.getSkuIdIndex(skuId)].curNum += 1;
        }
      }
    }

    // 减少单品数量
    public reduceCount(
      storeIndex: number,
      commodityIndex: number,
      curNum: number,
      skuId: string,
    ) {
      if (curNum - 1 === 0) {
        this.showDialog = true;
        this.isDialog = 0;
        this.warningMsg = '受不了了，宝贝不能再减少了哦～';
        this.closeDialog();
      } else {
        this.vaildCommodities[storeIndex].commodity_list[
          commodityIndex
        ].cur_cart_num -= 1;
        if (this.getSkuIdIndex(skuId) !== -1) {
          this.selectList[this.getSkuIdIndex(skuId)].curNum -= 1;
        }
      }
    }

    // 显示隐藏一个商铺下的所有商品的删除按钮
    public toggleDelete(storeIndex: number) {
      const curStoreDOM = this.$refs.getStoreDOM[storeIndex];
      const deleteDOM = curStoreDOM.querySelectorAll('.delete_commodity');
      const commodityDOM = curStoreDOM.querySelectorAll(
        '.commodity_item_container',
      );
      const editorTagDOM = curStoreDOM.querySelector('.get_edit');
      for (let i = 0, l = deleteDOM.length; i < l; i += 1) {
        deleteDOM[i].classList.toggle('show_delete');
        commodityDOM[i].classList.toggle('shrink_commodity_item');
      }
      if (editorTagDOM.innerText === '编辑') {
        editorTagDOM.innerText = '完成';
      } else {
        editorTagDOM.innerText = '编辑';
      }
    }

    // 左滑展示删除按钮
    public handleSwiper(storeId: string, skuId: string) {
      return (direction: string) => {
        const curCommodityDOM = document.querySelector(
          `#commodityItem_${storeId}_${skuId}`,
        );
        const curDeleteDOM = document.querySelector(
          `#delete_${storeId}_${skuId}`,
        );
        if (direction === 'left') {
          curDeleteDOM.classList.add('show_delete');
          curCommodityDOM.classList.add('shrink_commodity_item');
        } else {
          curDeleteDOM.classList.remove('show_delete');
          curCommodityDOM.classList.remove('shrink_commodity_item');
        }
      };
    }

    // 删除无效商品
    public deleteInvaidCommodities() {
      this.showDialog = true;
      this.warningMsg = '确定要删除失效产品吗？';
      this.isDialog = 1;
    }

    // 删除单品
    public deleteCommodity(
      curStoreIndex: number,
      curSkuIndex: number,
      curSkuId: string,
    ) {
      this.showDialog = true;
      this.warningMsg = '确定要删除这个宝贝吗？';
      this.isDialog = 2;
      this.curStoreIndex = curStoreIndex;
      this.curSkuIndex = curSkuIndex;
      this.curSkuId = curSkuId;
    }

    // 获取对话框传递过来的信息（确认删除无效商品 / 确认删除有效商品）
    public getDialogFeedback(type: number, confirmToDelete: boolean) {
      if (type === 1) {
        if (confirmToDelete) {
          this.invaildCommodities.splice(
            0,
            this.invaildCommodities.length,
          );
          this.showDialog = false;
        } else {
          this.showDialog = false;
        }
      } else if (confirmToDelete) {
        this.vaildCommodities[this.curStoreIndex].commodity_list.splice(
          this.curSkuIndex,
          1,
        );
        if (
          this.vaildCommodities[this.curStoreIndex].commodity_list
            .length === 0
        ) {
          this.vaildCommodities.splice(this.curStoreIndex, 1);
        }

        this.$emit('handleTotalNum', true);

        // 在删除一件商品时，如果这件商品已经被选中，那么selectList需要做出变化，相应的总价/总数量也要发生变化
        const result = this.getSkuIdIndex(this.curSkuId);
        if (result !== -1) {
          this.selectList.splice(result, 1);
        }
        this.showDialog = false;
      } else {
        this.showDialog = false;
      }
    }

    // 显示隐藏sku picker
    public handleSkuSelect() {
      const bodyDOM = document.querySelector('body') as HTMLBodyElement;
      bodyDOM.style.position = 'fixed';
      this.showSkuPicker = true;
    }

    // 获取Sku Picker反馈以关闭
    public getSkuPickFeedback(isClosePicker: boolean) {
      const bodyDOM = document.querySelector('body') as HTMLBodyElement;
      if (isClosePicker) {
        bodyDOM.style.position = 'relative';
        this.showSkuPicker = false;
      }
    }

    // 获取提交订单的反馈
    public getSubmitOrderFeedback() {
      if (this.selectList.length === 0) {
        this.showDialog = true;
        this.isDialog = 0;
        this.warningMsg = '您还没有选择宝贝哦～';
        this.closeDialog();
      } else {
        this.showDialog = true;
        this.isDialog = 0;
        this.warningMsg = '家里有矿啊，你还真买啊？！';
        this.closeDialog();
      }
    }

    // 定时关闭对话框
    public closeDialog() {
      setTimeout(() => {
        this.showDialog = false;
      }, 1000);
    }

    // 1 为天猫商铺, 2 为企业店铺
    public getStoreType(typeNum: number) {
      const type = storeType[typeNum];
      return !type ? false : type;
    }

    public getSelectAllFeedback() {
      if (this.selectListLength === this.vaildCommoditiesNum) {
        this.selectList = [];
      } else {
        this.vaildCommodities.forEach(store => {
          store.commodity_list.forEach(commodity => {
            if (!this.isCommoditySelected(commodity.sku_id)) {
              this.selectList.push({
                curNum: commodity.cur_cart_num,
                skuId: commodity.sku_id,
                unitPrice: commodity.sku_unit_price,
              });
            }
          });
        });
      }
    }
}
